const shadowHeader=()=>{const e=document.getElementById("header");this.scrollY>=50?e.classList.add("shadow"):e.classList.remove("shadow")};window.addEventListener("scroll",shadowHeader);const menuBtn=document.querySelector(".menu_btn"),closeBtn=document.querySelector(".close_btn"),navList=document.querySelector(".nav_links"),links=navList.querySelectorAll("li");function toggleNav(){navList.classList.toggle("top"),closeBtn.classList.toggle("display")}menuBtn.addEventListener("click",()=>{toggleNav()}),closeBtn.addEventListener("click",()=>{toggleNav()}),links.forEach(e=>{e.addEventListener("click",toggleNav)});